TabInfo.TabContent:
#include <iostream>
int main() {
    int* lengths = new int [2];
    std::string result = "NO";
    std::cin >> lengths[0] >> lengths[1];
    int** numbers = new int* [2];
    for (int iter = 0; iter < 2; iter++) {
        numbers[iter] = new int[lengths[iter]];
        for (int jter = 0; jter < lengths[iter]; jter++) {
            std::cin >> numbers[iter][jter];
        }
    }

    for (int iter = 0; iter < lengths[0]; iter++) {
        int number = numbers[0][iter];
        for (int jter = 0; jter < lengths[1]; jter++) {
            if (number + numbers[1][jter] == 10000) {
                result = "YES";
                break;
            }
        }

        if (result == "YES") break;
    }

    std::cout << std::endl << result << std::endl;
    return 0;
}
